import React from 'react';
import {connect} from 'react-redux';
import youtube from '../../apis/youtube';
import {showYouTubeVideos} from '../../actions/actions'


class Videos extends React.Component {

  componentDidMount() {
    async (selectedRecipeName) => {
      const response = await youtube.get(
        '/search', {
        params: {
          q: selectedRecipeName
        }
      });
      this.showYouTubeVideos(response.data.items);
    };
  }

  render() {
    return (
      <div>
      {this.props.videos.map((video) => {
        return (
          <div className="video-item">
            <a target="_blank" href={`https://youtube.com/watch?v=${this.props.video.id.videoId}`}>
              <img 
                src={this.props.video.snippet.thumbnails.medium.url} 
                alt={this.props.video.snippet.title} 
                key={this.props.video.id.videoId}
              />
            </a>
          </div>
        );
      })}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    selectedRecipeName: state.selectedRecipe.name,
    videos: state.videos
  }
};

export default connect(
  mapStateToProps,
  {showYouTubeVideos}
)(Videos);
