import React from 'react';
import {connect} from 'react-redux';
import Modal from '../../components/modal/Modal';
import RecipeForm from '../header/RecipeForm';
import history from '../history';


const CreateRecipe = (props) => {

  const onDismiss = () => {
    history.push('/');
  }

  const actions = () => {
    return(
      <React.Fragment>
        <button className="action-button confirm">Add Recipe</button>
        <button className="action-button cancel" onClick={onDismiss}>Cancel</button>
      </React.Fragment>
    );
  };

  return (
    <div>
      <Modal title="Add a Recipe" actions={actions} dismiss={onDismiss}>
        <RecipeForm />
      </Modal>
    </div>
  );
};

export default connect(
  null
)(CreateRecipe);
