import {TOGGLE_MOBILE_MENU} from '../actions/types';


const INITIAL_STATE = {
  mobileMenu: 'closed'
};

export default (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case TOGGLE_MOBILE_MENU:
      return state.mobileMenu === 'closed' ? 'open' : 'closed';
    default:
      return state;
  }
};
