import React from 'react';
import { Field, reduxForm } from 'redux-form';
import validate from './validate';


class WizardFormSecondPage extends React.Component {

  state = {
    cats: [{name:"", age:""}],
    owner: "",
    description: ""
  }
  handleChange = (e) => {
    if (["name"].includes(e.target.className) ) {
      let cats = [...this.state.cats]
      cats[e.target.dataset.id][e.target.className] = e.target.value.toUpperCase()
      this.setState({ cats }, () => console.log(this.state.cats))
    } else {
      this.setState({ [e.target.name]: e.target.value.toUpperCase() })
    }
  }

  addIngr = (e) => {
    this.setState((prevState) => ({
      cats: [...prevState.cats, {name:""}],
    }));
  }

  handleSubmit = (e) => { e.preventDefault() }

  render() {
    let {cats} = this.state
    return (
      <form onSubmit={this.handleSubmit} onChange={this.handleChange} >
        <label htmlFor="name">Ingredients</label> 
        <label onClick={this.addIngr}>Add another ingredient</label>
        {
          cats.map((val, idx)=> {
            let catId = `cat-${idx}`
            return (
              <div key={idx}>
                <label htmlFor={catId}>{`Cat #${idx + 1}`}</label>
                <input
                  type="text"
                  name={catId}
                  data-id={idx}
                  id={catId}
                  value={cats[idx].name} 
                  className="name"
                />
              </div>
            )
          })
        }
        <input type="submit" value="Submit" /> 
      </form>
    )
  }
}

export default reduxForm({
  form: 'recipeWizard', //Form name is same
  destroyOnUnmount: false,
  forceUnregisterOnUnmount: true, // <------ unregister fields on unmount
  validate
})(WizardFormSecondPage)
