import {connect} from 'react-redux';


const RecipeSelected = (props) => {

  return (
    <div className="selected-recipe">
      <div className="recipe-name">
        {props.selectedRecipe.recipe.recipeName}
      </div>
      <div className="prep-time">
        Prep Time: {props.selectedRecipe.recipe.prepTime} min
      </div>
      <div className="cook-time">
        Cook Time: {props.selectedRecipe.recipe.cookTime} min
      </div>
      <div className="">
        Oven Prep: {props.selectedRecipe.ovenPrep}
      </div>
      <div className="hardware">
        Hardware:
      </div>
      <div className="hardware-list">
        <ol>
          {/* {props.selectedRecipe.recipe.hardware.map((item, i) => <li key={i}>{item}</li>)} */}
        </ol>
        {props.selectedRecipe.name}
      </div>
      <div className="">
        {props.selectedRecipe.name}
      </div>
      <div className="">
        {props.selectedRecipe.name}
      </div>
      <div className="">
        {props.selectedRecipe.name}
      </div>
      <div className="">
        {props.selectedRecipe.name}
      </div>
    </div>
  )
};

const mapStateToProps = (state) => {
  return {
    selectedRecipe: state.selectedRecipe
  }
}

export default connect(mapStateToProps)(RecipeSelected);
