import React from 'react'

const renderField = ({ input, label, placeholder, type, meta: { touched, error } }) => (
  <div>
    <label>{label}</label>
    <div>
      <input 
        {...input} 
        placeholder={placeholder} 
        type={type} 
        autoComplete="off"
      />
      {touched && error && <span>{error}</span>}
    </div>
  </div>
)

export default renderField
