import React from 'react';

const renderInput = ({ input, label, placeholder, type, meta: { touched, error } }) => (
  <div className="form-field">
    <label>
      {label}
    </label>
    <div>
      <input 
        {...input} 
        placeholder={placeholder} 
        type={type} 
        autoComplete="off"
      />
      {touched && error && <span>{error}</span>}
    </div>
  </div>
)

export default renderInput
