import React from 'react';
import {connect} from 'react-redux';


const RecipeSelected = (props) => {

  if(!props.selectedRecipe.recipe === {}) {
    return <div></div>;
  }

  return (
    <div className="selected-recipe">
      <div className="recipe-name">
        {props.selectedRecipe.recipe.recipeName}
      </div>
      <div className="prep-time">
        Prep Time: {props.selectedRecipe.recipe.prepTime} min
      </div>
      <div className="cook-time">
        Cook Time: {props.selectedRecipe.recipe.cookTime} min
      </div>
      <div className="">
        Oven Prep: {props.selectedRecipe.recipe.ovenTemp}
      </div>
      <div className="hardware">
        Hardware:
      </div>
      <div className="hardware-list">
        <ol>
          {/* {props.selectedRecipe.recipe.hardware.map((item, i) => <li key={i}>{item}</li>)} */}
        </ol>
      </div>
      <div className="ingredients">
        Ingredients:
      </div>
      <div className="ingredients-list">
        <ol>
          {/* {props.selectedRecipe.recipe.ingredients.map((item, i) => <li key={i}>{item}</li>)} */}
        </ol>
      </div>
      <div className="directions">
        Directions:
      </div>
      <div className="directions-list">
        <ol>
          {/* {props.selectedRecipe.recipe.directions.map((item, i) => <li key={i}>{item}</li>)} */}
        </ol>
      </div>
    </div>
  )
};

const mapStateToProps = (state) => {
  return {
    selectedRecipe: state.selectedRecipe
  }
}

export default connect(mapStateToProps)(RecipeSelected);
