import {SHOW_ALL_RECIPES} from '../actions/types';


const INITIAL_STATE = {
  showAllRecipes: false
};

export default (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case SHOW_ALL_RECIPES:
      return action.payload;
    default:
      return state;
  }
};
