import React from 'react';
import {connect} from 'react-redux';
import PropTypes from 'prop-types';
import Modal from '../../components/modal/Modal';
// import WizardForm from '../forms/WizardForm';
import WizardFormFirstPage from '../forms/WizardFormFirstPage';
import WizardFormSecondPage from '../forms/WizardFormSecondPage';
import WizardFormThirdPage from '../forms/WizardFormThirdPage';
import WizardFormFourthPage from '../forms/WizardFormFourthPage';
import {createRecipe} from '../../actions/actions';
import history from '../history';
import {wizardPageNext} from '../../actions/actions';
import {wizardPagePrevious} from '../../actions/actions';
import ButtonNextContext from '../../contexts/ButtonNextContext';
import '../../css/forms/WizardForm.css';



const CreateRecipe = (props) => {

  const onDismiss = () => {
    history.push('/');
  }

  const onClickNext = () => {
    props.wizardPageNext;
  };

  const onClickPrev = () => {
    props.wizardPagePrevious;
  }

  const onSubmit = (formValues) => {
    props.createRecipe(formValues);
  };

  const buttonNext = () => {
    return <button type="submit" className="next" onClick={onClickNext}>Next</button>
  };

  const buttonPrev = () => {
    return <button type="button" className="previous" onClick={onClickPrev}>Previous</button>
  };

  const buttonSubmit = () => {
    return <button type="submit" className="submit" onSubmit={onClickSubmit}>Next</button>
  };




  return (
    <div>
      <Modal title="Add a Recipe" dismiss={onDismiss} page={props.page}>
        <div>
          {props.page === 1 && (
            <WizardFormFirstPage 
              initialValues={props.initialValues}
              onSubmit={onClickNext} 
            />
          )}
          {props.page === 2 && (
            <WizardFormSecondPage
              initialValues={props.initialValues}
              previousPage={previousPage}
              onSubmit={nextPage}
            />
          )}
          {props.page === 3 && (
            <WizardFormThirdPage
              initialValues={props.initialValues}
              previousPage={previousPage}
              onSubmit={nextPage}
            />
          )}
          {props.page === 4 && (
            <WizardFormFourthPage
              initialValues={props.initialValues}
              previousPage={previousPage}
              onSubmit={props.onSubmit}
            />
          )}
        </div>
      </Modal>
    </div>
  );
};

CreateRecipe.propTypes = {
  onSubmit: PropTypes.func.isRequired
}

const mapStateToProps = (state) => {
  return {page: state.wizardpage.page}
}

const mapDispatchToProps = (dispatch) => {
  return {
    createRecipe: (formValues) => dispatch(createRecipe(formValues)),
    wizardPageNext: () => dispatch(wizardPageNext()),
    wizardPagePrevious: () => dispatch(wizardPagePrevious()),
  }
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(CreateRecipe);
