import React from 'react';
import {connect} from 'react-redux'
import ReactDOM from 'react-dom';
import {destroy} from 'redux-form';
import {wizardPageNext} from '../../actions/actions';
import {wizardPagePrevious} from '../../actions/actions';
import '../../css/Modal.css';


const buttonBack = (props) => {
  if (props.page === 1) {
    return <div></div>
  }
  if (props.page >= 2) {
    return <button type="button" className="previous" onClick={props.previousPage}>Previous</button>
  }
}

const buttonForward = (props) => {
  if (props.page === 4) {
    return <button type="button" className="next" onClick={props.nextPage}>Submit</button>
  }
  if (props.page >= 1) {
    return <button type="button" className="next" onClick={props.nextPage}>Next</button>
  }
}


const Modal = (props) => { 
  console.log(props.page)
  const next = props.page === 4 ? 'Submit' : 'Next'
  console.log(next);
  return ReactDOM.createPortal(
    <div className="modal">
      <div className="modal-box">
        <div className="modal-title">
          {props.title}
          <i 
            className="fas fa-times close" 
            onClick={() => { 
              props.dismiss(); 
              props.destroy('recipeWizard');
            }}
          />
        </div>
        <div className="modal-content">
          {props.children}
        </div>
        <div className="modal-actions">
          <div className="action-back">
            <button>Previous</button>
          </div>
          <div className="action-forward">
            <button onClick={props.next}>{next}</button>
          </div>
        </div>
      </div>
    </div>,
    document.querySelector('#modal')
  );
};

const mapStateToProps = (state) => {
  return {page: state.wizardPage.page}
}

const mapDispatchToProps = (dispatch) => {
  return {
    destroy: (form) => dispatch(destroy(form)),
    next: () => dispatch(wizardPageNext),
    prev: () => dispatch(wizardPagePrevious)
  }
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Modal);
