import React from 'react';
import {connect} from 'react-redux';
import {showAllRecipes} from '../../actions/actions';
import '../../css/header/MenuOptions.css';


const MenuOptions = (props) => {

  const onShowAllClicked = (state) => {
    props.showAllRecipes(state);
  }

  return (
    <ul className="menu-options">
      <li>
        <label id="addRecipe">
          <i className="fas fa-plus" />
          <div>Add a Recipe</div>
        </label>
      </li>
      <li>
        <label id="showAllRecipes" onClick={onShowAllClicked}>
          <i className="fas fa-book-open" />
          <div>Show all Recipes</div>
        </label>
      </li>
      <li>
        <label id="inspireMe">
          <i className="far fa-lightbulb" />
          <div>Inspire Me</div>
        </label>
      </li>
    </ul>
  );
};

const mapStateToProps = (state) => {
  return {showAllRecipes: state.showAllRecipes}
};

export default connect(
  mapStateToProps,
  {showAllRecipes}
)(MenuOptions);
