import {SELECT_RECIPE_CARD} from '../actions/types';


const INITIAL_STATE = {
  id: '',
  name: ''
};

export default (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case SELECT_RECIPE_CARD:
      return {
        id: action.payload.id,
        name: action.payload.name
      };
    default:
      return state;
  }
};
