import React from 'react';
import { Field, reduxForm } from 'redux-form';
import validate from './validate';


class WizardFormSecondPage extends React.Component {

  state = {
    ingreds: [{name:""}]
  }

  handleChange = (e) => {
    if (["name"].includes(e.target.className) ) {
      let ingreds = [...this.state.ingreds]
      ingreds[e.target.dataset.id][e.target.className] = e.target.value.toUpperCase()
      this.setState({ ingreds })
    } else {
      this.setState({ [e.target.name]: e.target.value.toUpperCase() })
    }
  }

  addIngr = (e) => {
    this.setState((prevState) => ({
      ingreds: [...prevState.ingreds, {name:""}],
    }));
  }

  render() {
    return (
      <form onSubmit={this.props.handleSubmit} onChange={this.handleChange} >
        <label htmlFor="name">Ingredients</label> 
        <label onClick={this.addIngr}>Add another ingredient</label>
        {
          this.state.ingreds.map((val, idx)=> {
            let catId = `cat-${idx}`
            return (
              <div key={idx}>
                <label htmlFor={catId}>{`${idx + 1})`}</label>
                <input
                  type="text"
                  name={catId}
                  data-id={idx}
                  id={catId}
                  value={this.state.ingreds[idx].name} 
                  className="name"
                />
              </div>
            );
          })
        }
        <div>
          <button type="button" className="previous" onClick={this.props.previousPage}>
            Previous
          </button>
          <button type="submit" className="next">
            Next
          </button>
        </div>
    </form>
    )
  }
}

export default reduxForm({
  form: 'recipeWizard', //Form name is same
  destroyOnUnmount: false,
  forceUnregisterOnUnmount: true, // <------ unregister fields on unmount
  validate
})(WizardFormSecondPage)
