import React from 'react';
import { Field, reduxForm } from 'redux-form';
import validate from './validate';
import renderField from './renderField';

const WizardFormFirstPage = props => {
  const { handleSubmit } = props

  const typesOfMeals = [
    'breakfast', 
    'lunch', 
    'dinner', 
    'lunch or dinner', 
    'side dish',
    'soup',
    'salad',
    'appetizer',
    'dessert'
  ];

  const renderSelector = ({ input, label, options, meta: { touched, error } }) => (
    <div className="form-field">
      <label>{label}</label>
      <select {...input}>
        <option value="" disabled>Select ...</option>
        {{options}.map(val => (
          <option value={val} key={val}>
            {val}
          </option>
        ))}
      </select>
      {touched && error && <span>{error}</span>}
    </div>
  )

  const specialDiets = [
    'None',
    'Vegetarian', 
    'Vegan', 
    'Gluten-free', 
    'Keto', 
    'Paleo', 
    'Raw Food'
  ];

  const specialDietSelector = ({ input, label, meta: { touched, error } }) => (
    <div className="form-field">
      <label>{label}</label>
      <select {...input}>
        <option value="" disabled>Select ...</option>
        {specialDiets.map(val => (
          <option value={val} key={val}>
            {val}
          </option>
        ))}
      </select>
      {touched && error && <span>{error}</span>}
    </div>
  )

  // const renderField = ({ input, label, placeholder, type, meta: { touched, error } }) => (
  //   <div>
  //     <label>{label}</label>
  //     <div>
  //       <input {...input} placeholder={placeholder} type={type} />
  //       {touched && error && <span>{error}</span>}
  //     </div>
  //   </div>
  // )


  return (
    <form onSubmit={handleSubmit}>
      <Field
        name="recipeName"
        type="text"
        component={renderField}
        label="Recipe Name"
        placeholder="Recipe Name"
      />
      <Field
        name="link"
        type="text"
        component={renderField}
        label="Link"
        placeholder="Link to website"
      />
      <Field 
        name="typeOfMeal" 
        component={renderSelector} 
        options={typesOfMeals}
        label="Type of Meal " 
      />
      <Field 
        name="mainIngredient"
        type="text"
        component={renderField}
        label="What's the main ingredient?"
        placeholder="main ingredient"
      />
      <Field 
        name="typeOfMeal" 
        component={specialDietSelector}
        label="Fit a special diet? "
      />
      <Field 
        name="prepTime"
        type="tel"
        component={renderField}
        label="Prep Time"
      >
        min
      </Field>
      <Field 
        name="cookTime"
        type="tel"
        component={renderField}
        label="Cook Time"
      />
      <Field 
        name="hardware"
        type="text"
        component={renderField}
        label="Special hardware needed?"
        placeholder="roasting pan, skillet, whish, etc."
      />

      <div>
        <button type="submit" className="next">
          Next
        </button>
      </div>
    </form>
  )
}

export default reduxForm({
  form: 'wizard', // <------ same form name
  destroyOnUnmount: false, // <------ preserve form data
  forceUnregisterOnUnmount: true, // <------ unregister fields on unmount
  validate
})(WizardFormFirstPage)
