import React from 'react';
import Recipes from './Recipes';


const SearchResults = (props) => {

  let filteredRecipes = []
  if(props.showAll) {
    filteredRecipes = Recipes;
  } else {
    filteredRecipes = props.searchTerm.length > 0 ? 
      Recipes.filter((recipe) => 
        JSON.stringify(recipe).toLowerCase().indexOf(props.searchTerm.toLowerCase()) > -1) :
    [];    
  }

  return (
    <div>
      {filteredRecipes.map((recipe) => 
        <div 
          className="recipe-card"
          key = {recipe.id} 
          onClick = {() => {
            props.selectedRecipe(recipe.id, recipe.recipeName)
          }} 
        >
          {recipe.recipeName}
        </div>
      )}
    </div>
  );
};

export default SearchResults;
