import {SIGN_IN, SIGN_OUT, SEARCHING_RECIPES, TOGGLE_MOBILE_MENU, SHOW_ALL_RECIPES, SELECT_RECIPE_CARD} from './types';

export const signIn = (userId) => {
  return {
    type: SIGN_IN,
    payload: userId
  };
};

export const signOut = () => {
  return {
    type: SIGN_OUT,
  };
};

export const searchingRecipes = (term) => {
  return {
    type: SEARCHING_RECIPES,
    payload: term
  };
  // set selectedRecipeID: '', videos=[], showAllRecipes=false
};

export const toggleMobileMenu = () => {
  return {
    type: TOGGLE_MOBILE_MENU
  };
};

export const showAllRecipes = (bool) => {
  return {
    type: SHOW_ALL_RECIPES,
    payload: getState().showAllRecipes ? false : true
  };
};

export const selectRecipeCard = (id, name) => {
  return {
    type: SELECT_RECIPE_CARD,
    payload: {id, name}
  };
};
