import React from 'react';
import {connect} from 'react-redux';
import Recipes from './Recipes';
import '../../css/main/SearchResults.css';


const SearchResults = (props) => {
  let filteredRecipes = []
  if(props.showAllRecipes) {
    filteredRecipes = Recipes;
  } else {
    filteredRecipes = props.term.length > 0 ? 
      Recipes.filter((recipe) => 
        JSON.stringify(recipe).toLowerCase().indexOf(props.term.toLowerCase()) > -1) :
    [];    
  }

  return (
    <div>
      {filteredRecipes.map((recipe) => 
        <div 
          className="recipe-card"
          key = {recipe.id} 
          onClick = {() => {
            props.selectedRecipe(recipe.id, recipe.recipeName)
          }} 
        >
          {recipe.recipeName}
        </div>
      )}
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    term: state.searchingRecipes,
    showAllRecipes: state.showAllRecipes
  }
}

export default connect(mapStateToProps)(SearchResults);
