import {SEARCHING_RECIPES} from '../actions/types';


const INITIAL_STATE = {
  term: ''
};

export default (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case SEARCHING_RECIPES:
      return action.payload;
    default:
      return state;
  }
};
