import youtube from '../../apis/youtube';

import {
  SIGN_IN, 
  SIGN_OUT, 
  SEARCHING_RECIPES, 
  TOGGLE_BOOLEAN, 
  SELECT_RECIPE_CARD,
  FETCH_VIDEOS
} from './types';

export const signIn = (userId) => {
  return {
    type: SIGN_IN,
    payload: userId
  };
};

export const signOut = () => {
  return {
    type: SIGN_OUT,
  };
};

export const searchingRecipes = (term) => {
  return {
    type: SEARCHING_RECIPES,
    payload: term
  };
  // set selectedRecipeID: '', videos=[], showAllRecipes=false
};

export const toggleBoolean = (stateToToggle) => {
  (dispatch, getState) => {
    const newState = getState(stateToToggle) ? false : true 
    dispatch({type: TOGGLE_BOOLEAN, payload: newState})
  };
};


export const selectRecipeCard = (id, name) => {
  return {
    type: SELECT_RECIPE_CARD,
    payload: {id, name}
  };
};

