import Recipes from '../components/main/Recipes';

import {
  CREATE_RECIPE,
  DELETE_RECIPE,
  EDIT_RECIPE
} from '../actions/types';


const INITIAL_STATE = {
  recipes: Recipes
};

export default (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case CREATE_RECIPE:
      return { ...state, ...action.payload};
    //   return firebase.database().ref('recipes/').set(action.payload);
    case EDIT_RECIPE:
      return {...state, ...action.payload};
    case DELETE_RECIPE:
      return action.payload.id
    default:
      return state;
  }
};
